catalog_script_client.script:
function onSubmit() {
    //Type appropriate comment here, and begin script below

    var auth_pfile_val = g_form.getValue('qs_auth_profile');
    var approval_wf_extension = g_form.getValue('approval_workflow');
    var disc = g_form.getValue('discovery');
    var qkstart = g_form.getValue('quickstart');
    var retry = g_form.getValue('number_retries_to_be_performed');
	var anonymous = g_form.getValue('allow_anonymous_user');
	var oMidServer = g_form.getValue('enable_outbound_calls_using_mid_server');
    var midDet = g_form.getValue('mid_server_details');
    var cag = g_form.getValue('cloud_assignment_group');
    var cat = g_form.getValue('cloud_assigned_to');
    var ciag = g_form.getValue('configuration_item_assignment_group');
	var credId = g_form.getValue('mcmp_api_credential');
	var mcmpUrl = g_form.getValue('mcmp_endpoint');
	var adapVersion = g_form.getValue('adapterversion');

    var ga = new GlideAjax('Emp_Auth_Profile_Configuration');
    ga.addParam('sysparm_name', 'update_properties');
    ga.addParam('auth_name', auth_pfile_val);
    ga.addParam('approval_wf_name', approval_wf_extension);
    ga.addParam('discovery_enabled', disc);
    ga.addParam('qkstart_enabled', qkstart);
    ga.addParam('retry_count', retry);
	ga.addParam('anonymous_user', anonymous); //anonymous user
	ga.addParam('out_midserver', oMidServer);
    ga.addParam('mid_details', midDet);
    ga.addParam('cloud_ag', cag);
    ga.addParam('cloud_at', cat);
    ga.addParam('cloud_ci_ag', ciag);
	ga.addParam('adapterVersion', adapVersion);
    ga.addParam('api_credential', credId);
	ga.addParam('mcmp_url', mcmpUrl);
    ga.getXML(UpdateResponse);

    function UpdateResponse(response) {
        try {
            var answer = response.responseXML.documentElement.getAttribute("answer");

            if (answer != undefined && answer != '')
                alert(answer + "Updated Successfully");
        } catch (e) {
			alert("Exception "+e);
		}
    }
}
