catalog_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
        var ga = new GlideAjax('Emp_Auth_Profile_Configuration');
        ga.addParam('sysparm_name', 'getIntegratedCreds');
        ga.getXML(credsResponse);

    function credsResponse(response) {
    var intCreds = response.responseXML.documentElement.getAttribute("answer");
    var intCredvalues = JSON.parse(intCreds);
    if (intCredvalues) {
    g_form.setValue('adapterversion', intCredvalues.version);
//     g_form.setValue('mcmp_endpoint', intCredvalues.url);
//     g_form.setValue('mcmp_api_credential', intCredvalues.creds);

    var adapterversion = g_form.getValue('adapterversion');

    //             if (g_form.getValue('mcmp_endpoint') != '') {
    //             g_form.setReadOnly('account_name', true);
    //             }
    if (adapterversion == 'v3') {
        g_form.setDisplay('accesstags', true);
        g_form.setMandatory('accesstags', true);
        g_form.setMandatory('account_user', true);
        g_form.setMandatory('password', true);

        g_form.setDisplay('enable_common_discovery', false);
        //g_form.setDisplay('provisioning_using_servicenow', false); 
        g_form.setDisplay('additional_dataschema', false);
        g_form.setDisplay('external_context_list', false);
        g_form.setDisplay('qs_auth_profile', false);
        g_form.setDisplay('consume_namespace', false);
        g_form.setDisplay('quickstart', false);
        g_form.setDisplay('multistep_approval', false);

        g_form.setMandatory('purpose', false);
        g_form.setDisplay('purpose', false);

        g_form.setMandatory('contexts', false);
        g_form.setDisplay('contexts', false);

        g_form.setMandatory('ordertracking', false);
        g_form.setDisplay('ordertracking', false);

        g_form.setMandatory('externalapproval', false);
        g_form.setDisplay('externalapproval', false);

        g_form.setMandatory('inventory', false);
        g_form.setDisplay('inventory', false);

        g_form.setMandatory('external_context', false);
        g_form.setDisplay('external_context', false);
    } else {
        g_form.setDisplay('accesstags', false);
        g_form.setMandatory('accesstags', false);
        g_form.setMandatory('account_user', false);
        g_form.setMandatory('password', false);

        g_form.setDisplay('enable_common_discovery', true);
        //g_form.setDisplay('provisioning_using_servicenow', true);
        g_form.setDisplay('additional_dataschema', true);
        g_form.setDisplay('external_context_list', true);
        g_form.setDisplay('qs_auth_profile', true);
        g_form.setDisplay('consume_namespace', true);
        g_form.setDisplay('quickstart', true);
        g_form.setDisplay('multistep_approval', true);

        g_form.setMandatory('purpose', true);
        g_form.setDisplay('purpose', true);

        g_form.setMandatory('contexts', true);
        g_form.setDisplay('contexts', true);

        g_form.setMandatory('ordertracking', true);
        g_form.setDisplay('ordertracking', true);

        g_form.setMandatory('externalapproval', true);
        g_form.setDisplay('externalapproval', true);

        g_form.setMandatory('inventory', true);
        g_form.setDisplay('inventory', true);

        g_form.setMandatory('external_context', true);
        g_form.setDisplay('external_context', true);


    }

     }
     }
}
