sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    var crte_tsk_flg = current.parent.variables.var_create_changetask.toString();
	var provider = current.parent.variables.var_offering_provider;

    switch (crte_tsk_flg) {
        case 'true':
            var assgnGrp = gs.getProperty('x_ibmg3_hcms.emp_cloud.assignment_group');
            var assgnTo = gs.getProperty('x_ibmg3_hcms.emp_cloud.assigned_to');
            var grs = new GlideRecord('change_task');
            grs.addQuery('change_request', current.sys_id);
            grs.addQuery('assignment_group.name', assgnGrp);
            grs.addQuery('assigned_to.name', assgnTo);
            grs.addQuery('short_description', 'Provisioning Task');
            grs.query();
            if (grs.next()) {
                grs.state = 2;
				//If Provider type is snow, the on hold must be set to true
				if(provider == "snow") {
					grs.on_hold = true;
					grs.on_hold_reason = "Waiting for MCMP";
				}					
                grs.update();
            }
            break;

        case 'false':
            new HCMSLogLevel().LogMsg("info", "Provisioning Task Creation is not handled by Droplet Application for order: " + current.parent.request.x_ibmg3_hcms_order_number);
    }
})(current, previous);
