 * @author Asha Bagade 
 * @Business Rule updates the Request state based on Approval
 **/

(function executeRule(current, previous /*null when async*/ ) {
    //this BR is used to set the Request state and stage field based on change approval
    var st_tus = current.state;
    var req_rec;
    var apr_id = current.document_id; //fetch Change id
    var cart = gs.getProperty('x_ibmg3_hcms.mvp3.cart.enabled'); // property decides if mvp3 cart is enabled and approval is driven by request alone
    var req_item = new GlideRecord('sc_req_item');
    req_item.addQuery('x_ibmg3_hcms_related_change_request', apr_id);
    req_item.query();
    if (req_item.next()) {
        req_rec = req_item.request;
    }

    //update the request stage
    var reqrec = new GlideRecord('sc_request');
    reqrec.addQuery('sys_id', req_rec);
    reqrec.query();
    if (reqrec.next()) {
        //when cart is enabled and approval response to EM is request driven, once change is approved Request stage is set to fulfillment
        if (cart == "true") {
            if (st_tus == 'approved') {
                var qry = 'sysapproval=' + apr_id + '^state=requested';
                var gr = new GlideRecord('sysapproval_approver');
                gr.addEncodedQuery(qry);
                gr.query();
                if (!gr.next()) {
                    reqrec.stage = 'fulfillment';
                    new HCMSLogLevel().LogMsg("info", current.document_id.getDisplayValue() + " Approved , Request " + reqrec.number + " moved to Fulfillment");
                    reqrec.update();
                }
            } 

			else if (st_tus == 'rejected') {
                new HCMSLogLevel().LogMsg("info", current.document_id.getDisplayValue() + " Rejected");
                //set catalog task to incomplete which inturn will set RITM state via WF
                var catTsk = new GlideRecord('sc_task');
                catTsk.addQuery('request_item', req_item.sys_id);
                catTsk.query();
                if (catTsk.next()) {
                    catTsk.state = 4;
                    catTsk.update();
                }
            }
        } 

		else {
            //For non cart approval response to EM would be sent after change approval, once chg is approved/rejected Request state and stage are set
            if (st_tus == 'approved') {
                reqrec.approval = 'approved';
                reqrec.stage = 'fulfillment';
                new HCMSLogLevel().LogMsg("info", current.document_id.getDisplayValue() + " Approved , Request " + reqrec.number + " moved to Fulfillment");
            } else if (st_tus == 'rejected') {
                reqrec.approval = 'rejected';
                new HCMSLogLevel().LogMsg("info", current.document_id.getDisplayValue() + " Rejected");
            }
            reqrec.update();
        }
    }
})(current, previous);
