sys_script_include.script:
var cancelAfterFailure = Class.create();
cancelAfterFailure.prototype = {
    initialize: function() {},

    changeCatalogTask: function(orderNumber) {
        var req_num, chg_num, worknote, worknoteContent, lastWorknote;
        var flag = false;

		//fetch the Request
        var sr = new GlideRecord('sc_request');
        sr.addQuery('x_ibmg3_hcms_order_number', orderNumber);
        sr.query();
        if (sr.next()) {
            req_num = sr.sys_id; 
        }

		// Fetch th erelated change request
        var ritm = new GlideRecord('sc_req_item');
        ritm.addQuery('request', req_num);
        ritm.query();
        if (ritm.next()) {
            chg_num = ritm.x_ibmg3_hcms_related_change_request;
        }

		//Check if linked to incident
        var taskgr = new GlideRecord('incident');
        taskgr.addQuery('rfc', chg_num);
        taskgr.query();
        if (taskgr.next()) {
            flag = true;
        } else {
            flag = false;
        }
        return flag;
    },
    type: 'cancelAfterFailure'
};
