sys_script_include.script:
var HCMSCreateTasksRequestedItems = Class.create();
HCMSCreateTasksRequestedItems.prototype = {
    initialize: function() {},

    createBrokerTasks: function(current) {
        try {
            //Create the task record for all requested items in the requested item array
            var reqItemGR = new GlideRecord('sc_req_item');
            reqItemGR.addQuery('request.number', current.number);
            reqItemGR.query();
            while (reqItemGR.next()) {
                var gr = new GlideRecord('sc_task');
                gr.initialize();
				gr.short_description =  reqItemGR.short_description;
                gr.request_item.setDisplayValue(reqItemGR.number);
                gr.state = '1';
                gr.insert();				
            new HCMSLogLevel().LogMsg("info", "*Catalog Task created for order: " + reqItemGR.variables.order_number + " : " + gr.number);
            }
        } catch (ex) {
            workflow.error("Kyndryl Marketplace Provision Workflow: Create Tasks for Requested Items: " + this.type + "(" + err.message + ")");
        }
    },
    type: 'HCMSCreateTasksRequestedItems'
};
