sys_script_include.script:
var Emp_ServiceNow_Configuration = Class.create();
Emp_ServiceNow_Configuration.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


    mcmp_Version_Update: function() {

        var version_val = this.getParameter('version_name');
        var sys_rest, val;
        switch (version_val) {

            case "v3":

                sys_rest = new GlideRecord('sys_rest_message_fn');
                sys_rest.addEncodedQuery('rest_message=38a4e189db0160502019d90d6896194b^function_nameLIKEv3');
                sys_rest.query();
                while (sys_rest.next()) {
                    val = sys_rest.rest_endpoint.toString().replace("v2", "v3");
                    sys_rest.rest_endpoint = val;
                    sys_rest.update();	
                }
                break;


            case "v2":
                sys_rest = new GlideRecord('sys_rest_message_fn');
                sys_rest.addEncodedQuery('rest_message=38a4e189db0160502019d90d6896194b^http_method=post');
                sys_rest.query();
                while (sys_rest.next()) {
                    val = sys_rest.rest_endpoint.toString().replace("v3", "v2");
                    sys_rest.rest_endpoint = val;
                    sys_rest.update();

                }


        }

		return "Version Updated Successfully To "+version_val;

    },


	updateAdapterVersion : function(){

		var rec = new GlideRecord('sys_properties');
        rec.addQuery('name', 'x_ibmg3_hcms.AdapterVersionConfiguration');
        rec.query();
        if(rec.next()){
        rec.value = 'v2';
		rec.update();
       }
	},


    type: 'Emp_ServiceNow_Configuration'
});
