sys_script_include.script:
var HCMSGetTenatDetails = Class.create();
HCMSGetTenatDetails.prototype = {
    initialize: function() {},

    get_tenant_details: function(brokerlocation, broker_domain) {
        var apiKeyVal;
        var UserVal;
        //Fetch the api key and username based on broker location
        var gr_loc = new GlideRecord('api_key_credentials');
        gr_loc.addQuery('name', brokerlocation);
		gr_loc.addQuery('sys_domain',broker_domain);
        gr_loc.query();
        if (gr_loc.next()) {
            var retrieveDetails = new global.MCMPCredentialsStorage();
            apiKeyVal = retrieveDetails.getCredApiKey_HcmsBroker(gr_loc.api_key);
            UserVal = gr_loc.user_name;
        }
        return {
            ApiKey: apiKeyVal,
            UserName: UserVal
        };
    },

    get_order_location: function(change_req_sys_id) {
        var req_loc;
        var sc_req_loc = new GlideRecord('sc_req_item');
        sc_req_loc.addQuery('x_ibmg3_hcms_related_change_request', change_req_sys_id);
        sc_req_loc.query();
        if (sc_req_loc.next()) {
            req_loc = sc_req_loc.request.x_ibmg3_hcms_cloud_broker_location;
        }
        return req_loc;
    },

    QSCheckDS: function() {
        var plg = new GlideRecord('sys_plugins');
        plg.addQuery('source', 'com.glide.domain');
        plg.query();
        plg.next();
        if (plg.active == true) {
            return 'true';
        } else {
            return 'false';
        }
    },

	getRequestDomain: function(order_number){

		var req_val = new GlideRecord('sc_request');
		req_val.addQuery('x_ibmg3_hcms_order_number',order_number);
		req_val.query();
		if(req_val.next()){
			return req_val.sys_domain.toString();
		}

	},

	getRequesUItDomain: function(cred){

		var req_val = new GlideRecord('api_key_credentials');
		req_val.addQuery('name',cred);
		req_val.query();
		if(req_val.next()){
			return req_val.sys_domain.toString();
		}

	},

    type: 'HCMSGetTenatDetails'
};
