sys_script_include.script:
var HCMS_generate_Sys_id = Class.create();
HCMS_generate_Sys_id.prototype = {
    initialize: function() {},

    //Fetch the sys_id of service_collection
    Populate_ServiceCOLL_sysid: function(service_collection) {
        var srCOLL = new GlideRecord('u_qs_cmdb_ci_servicecollection');
        srCOLL.get(service_collection);
        srCOLL.query();
        if (srCOLL.next()) {
            return srCOLL.getUniqueValue();
        }
    },

    //Fetch the company sys_id
    Populate_srCustomer_sysid: function(customer_id) {
        var srCust_id = new GlideRecord('core_company');
        srCust_id.get(customer_id);
        srCust_id.query();
        if (srCust_id.next()) {
            return srCust_id.getUniqueValue();
        }
    },

    //Fetch the Business app sys_id
    Populate_srBusinessAPP_sysid: function(Business_ID) {
        var srBusiness_ID = new GlideRecord('cmdb_ci_business_app');
        srBusiness_ID.get(Business_ID);
        srBusiness_ID.query();
        if (srBusiness_ID.next()) {
            return srBusiness_ID.getUniqueValue();
        }
    },

    //Fetch the Req for user's sys_id
    populateRequestedFor_sysId: function(reqfor_email) {
        var usrId = new GlideRecord('sys_user');
        usrId.addEncodedQuery('email=' + reqfor_email + '^ORuser_name =' + reqfor_email);
        usrId.query();
        if (usrId.next()) {
            return usrId.getUniqueValue();
        }
    },

    //Fetch the AD domain's sys_id
    populateADdomain_sysId: function(adDomain_name) {
        var adDomain = new GlideRecord('cmdb_ci_appl_active_directory');
        if (adDomain.get('name', adDomain_name)) {
            return adDomain.getUniqueValue();
        }
    },

    //Fetch the resource's(ci) sys_id stored on day1's RITM as part of ci update call
    populateCi_sysId: function(serviceOfferingInsID) {
        try {
            var query = 'active=false^correlation_idLIKE' + serviceOfferingInsID;
            var chgReq;
            var ritm = new GlideRecord('sc_req_item');
            ritm.addEncodedQuery(query);
            ritm.query();
            while (ritm.next()) {
                if ((ritm.variables.ITDSYSIDSIX))//the variable is available only for Day1 requests
                    return ritm.variables.ITDSYSIDSIX;
            }
        } catch (ex) {
            new HCMSLogLevel().LogMsg('error', 'populateCi_sysId error ' + ex);
        }
    },

	//Fetch the sys_id of the Authenticaion Profile to re-direct to QS API

	qs_auth_profile_generate_id : function(authName){

		var qs_auth_name = authName;
		var qsr = new GlideRecord('sys_auth_profile_basic');
		qsr.addQuery('name',qs_auth_name);
		qsr.query();
		if(qsr.next()){
			return qsr.sys_id;
		}

	},

    type: 'HCMS_generate_Sys_id'
};
