//script to wait until requestapprovalhold is unchecked and to move the wf forward for CI and Change creation
try {
    var grTask = new GlideRecord("sc_request");
    grTask.addQuery('sys_id', current.request);    
    grTask.query();
    if (grTask.next()) {
		if(grTask.x_ibmg3_hcms_requestapprovalhold == true){
		answer = false;
		}        
		else
			answer = true;
    } else {
        answer = true;
    }
} catch (err) {
    workflow.info("Requested Item Workflow: Wait For Approval to unhold:  " + err.message);
}

