try {
    var grTask = new GlideRecord("sc_task");
    grTask.addQuery('request_item', current.sys_id);
    grTask.addQuery('state', '!=', '3');
    grTask.addQuery('state', '!=', '4');
    grTask.addQuery('state', '!=', '7');
    grTask.addQuery('wf_activity', '');
    grTask.query();
    if (grTask.next()) {
        //tasks found in other states than 'Closed complete', 'Closed incomplete', or 'Closed Skipped'
        answer = false;
    } else {
        //no tasks found in states other than 'Closed complete', 'Closed incomplete', or 'Closed Skipped'
        answer = true;
    }
} catch (err) {
    workflow.info("Requested Item Workflow: Wait For Task Completion:  " + this.type + "(" + err.message + ")");
}

