try {
    var chgType = '';
    var gr = new GlideRecord('sc_req_item');
    gr.addQuery('request=' + current.sys_id);
    gr.query();
    if (gr.next()) {
        chgType = gr.variables.var_change_type;
        if (gr.variables.var_change_type == 'standard')
            answer = "no";
        else
            answer = "yes";
    }
    new HCMSLogLevel().LogMsg("info", "Change type is " + chgType + " for " + current.x_ibmg3_hcms_order_number);
} catch (e) {
    workflow.error("HCMS Provision Workflow: Check if Change Type normal or standard : "+ e);
}

