//check if catalog task is closed incomplete
function validateTaskstatus() {
	var sgc_task = new GlideRecord('sc_task');
	sgc_task.addQuery('request_item.request='+current.sys_id);
	sgc_task.query();
	while(sgc_task.next()) {
		if (sgc_task.state == '4') {//closed incomplete state
			return 'yes';
		}
	}
	return 'no';
}

