//Run a query to find all the requested items for a service catalog request 
//Find items that are NOT 'Closed complete', 'Closed incomplete', or 'Closed Skipped'
//And that are not governed by workflow
//If there are any, then the workflow will wait

try {
var gr_requestedItems = new GlideRecord("sc_req_item");
gr_requestedItems.addQuery('request', current.sys_id);
gr_requestedItems.addQuery('state', '!=', '3');
gr_requestedItems.addQuery('state', '!=', '4');
gr_requestedItems.addQuery('state', '!=', '7');
gr_requestedItems.addQuery('wf_activity', '');
gr_requestedItems.query();
if (gr_requestedItems.next()) {
		//requested items found in other states than 'Closed complete', 'Closed incomplete', or 'Closed Skipped'
		answer = false;
	} else {
		answer = true;
	}
}
catch(err) {
   workflow.error("Broker Provision Workflow: Wait for Requested Items Completion: " + this.type + "(" + err.message + ")" );
}

