function ifDiscoveryEnabled() {
    try {
        var discover = gs.getProperty('x_ibmg3_hcms.mvp3.discovery.enabled');
        if (discover == "false") {
            return 'yes';
        }
        return 'no';
    } catch (e) {
        workflow.error("HCMS Provision Workflow: Check if discovery is enabled or disabled : " + this.type + " " + e);
    }
}

